import { Toolbar, ToolbarContent, ToolbarItem, Button, Switch } from '@patternfly/react-core';
import { LogViewer as LogViewerPF, LogViewerSearchProps } from '@patternfly/react-log-viewer';
import { useEffect, useRef, useState } from 'react';
import '../../index.css';
import { CaretDownIcon, CaretUpIcon } from '@patternfly/react-icons';

interface ILogViewerProps {
  data: string | string[];
  follow: boolean;
}

export const LogViewer = ({ data, follow }: ILogViewerProps) => {
  const logViewerRef = useRef<any>();

  const [buffer, setBuffer] = useState<any>(data);
  const [isPaused, setIsPaused] = useState(false);

  // line count assigned to 'scrollToRow' makes it scroll to bottom properly
  const [lineCount, setLineCount] = useState<any>(0);
  const [following, setFollowing] = useState<boolean>(follow);

  useEffect(() => {
    if (following) {
      setLineCount(data.length);
      logViewerRef?.current?.scrollToBottom();
    }
  }, [following, data]);

  useEffect(() => {
    if (!isPaused && data.length > 0) {
      setIsPaused(true);
    }
  }, [isPaused, buffer]);

  const onScroll = ({ scrollOffsetToBottom, scrollDirection, scrollUpdateWasRequested }: any) => {
    if (!scrollUpdateWasRequested) {
      if (scrollOffsetToBottom > 0) {
        // setIsPaused(true);
      } else {
        setIsPaused(false);
      }
    }
  };

  const FooterButton = () => {
    const handleClick = (e) => {
      setIsPaused(false);
    };
    return (
      <Button onClick={handleClick} isBlock>
        <OutlinedPlayCircleIcon />
        resume {linesBehind === 0 ? null : `and show ${linesBehind} lines`}
      </Button>
    );
  };

  return (
    <div className="p-10">
      <LogViewerPF
        innerRef={logViewerRef}
        scrollToRow={following ? lineCount : 0}
        data={buffer}
        toolbar={
          <Toolbar>
            <ToolbarContent>
              <ToolbarItem>
                <Button
                  onClick={() => {
                    logViewerRef?.current?.scrollTo(0, 0);
                  }}
                  variant="control"
                  icon={<CaretUpIcon />}
                >
                  Top
                </Button>
              </ToolbarItem>
              <ToolbarItem>
                <Button
                  onClick={() => {
                    logViewerRef?.current?.scrollToBottom();
                  }}
                  variant="control"
                  icon={<CaretDownIcon />}
                >
                  Bottom
                </Button>
              </ToolbarItem>
              <ToolbarItem>
                <Switch
                  label="Following"
                  labelOff="Not Following"
                  isChecked={following}
                  onChange={(checked) => {
                    setFollowing(checked);
                  }}
                />
              </ToolbarItem>
            </ToolbarContent>
          </Toolbar>
        }
        footer={isPaused && <FooterButton />}
        onScroll={onScroll}
      />
    </div>
  );
};
