import deepmerge from 'deepmerge';
import { Operation, compare } from 'fast-json-patch';

export interface JSONPatchItem {
  op: 'add' | 'remove' | 'replace' | 'copy' | 'move' | 'test';
  path: string;
  value?: any;
  from?: string;
}

export const createSafePatch = (original: Object | any[], modified: Object | any[]): Operation[] => {
  return compare(original, deepmerge(original, modified));
};

export const createDestructivePatch = (original: Object | any[], modified: Object | any[]): JSONPatchItem[] => {
  return compare(original, modified);
};
