import { Button, InputGroup, Select, SelectOption, SelectVariant } from '@patternfly/react-core';
import { SortAmountDownAltIcon, SortAmountDownIcon } from '@patternfly/react-icons';
import { useCallback, useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

import { getComponentQueryParamValue, updateQueryParamsInURL } from '../../utils/queryParamsHelper';

export enum SORT_ORDER {
  Asc = 'asc',
  Desc = 'desc',
}

interface ISortAttribute {
  /**
   * ID has to match object key {@link ISortOptions}, there is automatic checker throwing errors if they don't match.
   */
  id: string;
  /**
   * Sorting option title.
   */
  title: string;
  /**
   * Should be sorted by default by this attribute?
   * Maximally one attribute should be default.
   */
  isDefaultAttribute?: boolean;
  /**
   * If sorted by default by this attribute, which order is used?
   */
  defaultSortOrder?: SORT_ORDER;
}

export interface ISortOptions {
  [key: string]: ISortAttribute;
}

interface ISortingProps {
  sortOptions: ISortOptions;
  componentId: string;
}

export const Sorting = ({ sortOptions, componentId }: ISortingProps) => {
  const location = useLocation();
  const navigate = useNavigate();

  const [isFirstRender, setIsFirstRender] = useState<boolean>(true);

  const defaultAttributeKey = Object.entries(sortOptions).filter(([_, v]) => v.isDefaultAttribute)[0]?.[0];
  const defaultSortOrder = sortOptions[defaultAttributeKey]?.defaultSortOrder || SORT_ORDER.Asc;

  // attribute by which list is sorted
  const [sortAttribute, setSortAttribute] = useState<ISortAttribute | undefined>();
  const [sortOrder, setSortOrder] = useState<SORT_ORDER>(SORT_ORDER.Asc);
  const [isSortSelectOpen, setIsSortSelectOpen] = useState<boolean>(false);

  /**
   * Add sort param by updating URL.
   */
  const addSortFilter = useCallback(
    (filterAttribute: string, order: SORT_ORDER, replace: boolean = false) => {
      updateQueryParamsInURL({ sort: `=${order}=${filterAttribute}`, pageIndex: 1 }, componentId, location, navigate, replace);
    },
    [componentId, location, navigate]
  );

  /**
   * Delete sort param from URL.
   */
  const deleteSortFilter = () => {
    updateQueryParamsInURL({ sort: '', pageIndex: 1 }, componentId, location, navigate);
  };

  /**
   * Sychronize UI with the URL sort param.
   */
  useEffect(() => {
    const currentSortParam = getComponentQueryParamValue(location.search, 'sort', componentId) || '';

    if (currentSortParam) {
      const sortOrderUrl = currentSortParam.split('=')?.[1];
      const sortParam = currentSortParam.split('=')?.[2];

      setSortOrder(sortOrderUrl as SORT_ORDER);
      setSortAttribute(sortOptions[sortParam]);
    } else {
      setSortAttribute(undefined);
    }
  }, [location.search, location, componentId, navigate, sortOptions, defaultAttributeKey, addSortFilter]);

  /**
   * Add default sorting (if any) and check sortOptiions validity.
   */
  useEffect(() => {
    const currentSortParam = getComponentQueryParamValue(location.search, 'sort', componentId) || '';
    if (!currentSortParam && isFirstRender && defaultAttributeKey) {
      addSortFilter(defaultAttributeKey, defaultSortOrder, true);
    }

    if (isFirstRender) {
      setIsFirstRender(false);
    } else {
      Object.entries(sortOptions).forEach(([k, v]) => {
        // check sort options validity
        if (k !== v.id) {
          console.error('sortOptions: ', sortOptions);
          throw new Error(`sortOptions have invalid format, object key (${k}) has to match id field (${v.id})!`);
        }
      });
    }
  }, [isFirstRender, defaultAttributeKey, defaultSortOrder, addSortFilter, componentId, location, sortOptions]);

  return (
    <InputGroup>
      <Select
        hasPlaceholderStyle
        placeholderText="Sort"
        variant={SelectVariant.single}
        onToggle={(isOpen) => {
          setIsSortSelectOpen(isOpen);
        }}
        onSelect={(_, selection) => {
          setIsSortSelectOpen(false);
          addSortFilter((selection as ISortAttribute)?.id, sortOrder);
        }}
        selections={sortAttribute}
        isOpen={isSortSelectOpen}
      >
        {[
          ...Object.keys(sortOptions).map((filterAttributeKey: string) => {
            const filterAttribute = sortOptions[filterAttributeKey];
            // use 'title' attribute as default
            filterAttribute.toString = () => {
              return filterAttribute.title;
            };

            return <SelectOption key={filterAttribute.id} value={filterAttribute} />;
          }),
        ]}
      </Select>

      <Button
        onClick={() => {
          addSortFilter(sortAttribute!.id, sortOrder === SORT_ORDER.Asc ? SORT_ORDER.Desc : SORT_ORDER.Asc);
        }}
        variant="plain"
        isDisabled={!sortAttribute}
        icon={sortOrder === SORT_ORDER.Asc ? <SortAmountDownAltIcon /> : <SortAmountDownIcon />}
      ></Button>

      {sortAttribute && (
        <Button
          style={{ padding: '1px' }}
          onClick={() => {
            deleteSortFilter();
          }}
          variant="plain"
          isInline
        >
          Remove sorting
        </Button>
      )}
    </InputGroup>
  );
};
