import { Select, SelectOption, SelectVariant } from '@patternfly/react-core';
import { AxiosRequestConfig } from 'axios';
import { useCallback, useEffect, useRef, useState } from 'react';

interface ISearchSelect {
  attribute: string;
  callback: Function;
  timeout?: number;
}

export const SearchSelect = ({ attribute, callback, timeout = 200 }: ISearchSelect) => {
  const [selectOptions, setSelectOptions] = useState<any>([]);
  const [isSelectOpen, setIsSelectOpen] = useState<boolean>(false);
  const [selected, setSelected] = useState<any>();
  const timer = useRef<any>();
  const [defaultData, setDefaultData] = useState<any>([]);
  const [isLoading, setIsLoading] = useState<boolean>(false);

  const fetchData = useCallback(
    (val: string = '', setDefault: boolean = false) => {
      setIsLoading(true);
      const config: AxiosRequestConfig = { params: { pageSize: 20 } };
      if (val !== '') config.params.q = `${attribute}=like="%${val}%"`;
      callback(config).then((response: any) => {
        const data = response.data.content
          .map((obj: any) => {
            return {
              value: obj[attribute],
            };
          })
          .map((option: any, index: any) => <SelectOption key={index} value={option.value} />);
        setSelectOptions(data);
        if (setDefault) setDefaultData(data);
        return data;
      });
    },
    [callback, attribute]
  );

  useEffect(() => {
    fetchData('', true);
  }, [fetchData]);

  const clearSelection = () => {
    setSelected(null);
    setIsSelectOpen(false);
    setSelectOptions(defaultData);
  };

  useEffect(() => {
    setIsLoading(false);
  }, [selectOptions]);

  return (
    <Select
      variant={SelectVariant.typeahead}
      onToggle={(isOpen) => {
        setIsSelectOpen(isOpen);
      }}
      onSelect={(event, selection, isPlaceholder) => {
        if (isPlaceholder) clearSelection();
        else {
          if (event) {
            // setSelectOptions([<SelectOption key={0} value={selection} />]);
            fetchData(selection as string);
          }
          setSelected(selection);
          setIsSelectOpen(false);
        }
      }}
      onTypeaheadInputChanged={(val: string) => {
        if (val !== '') {
          clearTimeout(timer?.current);
          timer.current = setTimeout(() => fetchData(val), timeout);
        } else {
          setSelectOptions(defaultData);
        }
      }}
      isInputFilterPersisted={true}
      isInputValuePersisted={true}
      onClear={clearSelection}
      onFilter={() => {
        // filtering is done above
        return selectOptions;
      }}
      selections={selected}
      isOpen={isSelectOpen}
      hasInlineFilter={true}
      shouldResetOnSelect={false}
      loadingVariant={isLoading ? 'spinner' : undefined}
    >
      {selectOptions}
    </Select>
  );
};
