import { AUTH_ROLE, keycloakService } from '../../services/keycloakService';
import { ErrorPage } from '../ErrorPage/ErrorPage';
import styles from './ProtectedContent.module.css';

interface IProtectedContentProps {
  title: string;
  role?: AUTH_ROLE;
}

export const ProtectedContent = ({ children, title, role = AUTH_ROLE.User }: React.PropsWithChildren<IProtectedContentProps>) => {
  if (!keycloakService.isKeycloakAvailable) {
    return <ErrorPage pageTitle={title} errorDescription="Keycloak uninitialized."></ErrorPage>;
  }

  if (keycloakService.isKeycloakAvailable) {
    if (keycloakService.isAuthenticated()) {
      if (keycloakService.hasRealmRole(role)) {
        return <>{children}</>;
      } else {
        return <div className={styles['disable-content']}>{children}</div>;
      }
    } else {
      return <>{children}</>;
    }
  }

  return <div className={styles['disable-content']}>{children}</div>;
};
