import { Select, SelectOption, SelectVariant } from '@patternfly/react-core';
import { useEffect, useState } from 'react';

interface ISearchSelect {
  attribute: string;
  callback: Function;
}

export const SearchSelect = ({ attribute, callback }: ISearchSelect) => {
  const [defaultOptions, setDefaultOptions] = useState<any>([]);
  const [selectOptions, setSelectOptions] = useState<any>([]);
  const [isSelectOpen, setIsSelectOpen] = useState<boolean>(false);
  const [selected, setSelected] = useState<any>();

  useEffect(() => {
    callback({ params: { pageSize: 20 } }).then((response: any) => {
      setSelectOptions(
        response.data.content.map((obj: any) => {
          return {
            value: obj[attribute],
          };
        })
      );
      setDefault;
    });
  }, [callback, attribute]);

  const clearSelection = () => {
    setIsSelectOpen(false);
    setSelectOptions(defaultSelectOptions);
    setSelected('');
    console.log('B>>>');
  };

  return (
    <Select
      variant={SelectVariant.typeahead}
      onToggle={(isOpen) => {
        setIsSelectOpen(isOpen);
      }}
      onSelect={(event, selection, isPlaceholder) => {
        if (isPlaceholder) clearSelection();
        else {
          setSelected(selection);
          setIsSelectOpen(false);
        }
      }}
      onTypeaheadInputChanged={(val: string) => {
        callback({ params: { pageSize: 20, q: `${attribute}=like="%${val}%"` } }).then((response: any) => {
          setSelectOptions(
            response.data.content.map((obj: any) => {
              return {
                value: obj[attribute],
              };
            })
          );
        });
      }}
      onClear={clearSelection}
      selections={selected}
      isOpen={isSelectOpen}
    >
      {selectOptions.map((option: any, index: any) => (
        <SelectOption key={index} value={option.value} />
      ))}
    </Select>
  );
};
