import styles from './TopBar.module.css';
import { Flex, FlexItem } from '@patternfly/react-core';
import { ExclamationCircleIcon, InfoCircleIcon, WarningTriangleIcon } from '@patternfly/react-icons';

interface ITopBar {
  type: string;
  icon: any;
}

interface ITopBarSpecialized {}

export const TopBar = ({ children, type, icon }: React.PropsWithChildren<ITopBar>) => (
  <div className={`${styles['top-bar']} ${styles[type]}`}>
    <Flex spaceItems={{ default: 'spaceItemsSm' }}>
      <FlexItem>{icon}</FlexItem>
      <FlexItem>{children}</FlexItem>
    </Flex>
  </div>
);

export const TopBarError = ({ children }: React.PropsWithChildren<ITopBarSpecialized>) => (
  <div className={`${styles['top-bar']} ${styles['top-bar-error']}`}>
    <Flex spaceItems={{ default: 'spaceItemsSm' }}>
      <FlexItem>{<ExclamationCircleIcon color="white" />}</FlexItem>
      <FlexItem className={styles['top-bar-error-text']}>{children}</FlexItem>
    </Flex>
  </div>
);

export const TopBarWarning = ({ children }: React.PropsWithChildren<ITopBarSpecialized>) => (
  <TopBar type="top-bar-warning" icon={<WarningTriangleIcon />}>
    {children}
  </TopBar>
);

export const TopBarInfo = ({ children }: React.PropsWithChildren<ITopBarSpecialized>) => (
  <TopBar type="top-bar-info" icon={<InfoCircleIcon />}>
    {children}
  </TopBar>
);
