import styles from './TopBar.module.css';
import { Flex, FlexItem, IconProps } from '@patternfly/react-core';
import { ExclamationCircleIcon, InfoCircleIcon, WarningTriangleIcon, BuildIcon } from '@patternfly/react-icons';

interface ITopBar {
  type: string;
  icon: React.ComponentType<any>;
}

export const TopBar = ({ childrenm, type, icon }: React.PropsWithChildren<ITopBar>) => (
  <div className={`${styles['top-bar']} ${styles[type]}`}>
    <Flex spaceItems={{ default: 'spaceItemsSm' }}>
      <FlexItem>
        <ExclamationCircleIcon />
      </FlexItem>
      <FlexItem>{children}</FlexItem>
    </Flex>
  </div>
);

export const TopBarError = ({ children }: React.PropsWithChildren<ITopBar>) => (
  <div className={`${styles['top-bar']} ${styles['top-bar-error']}`}>
    <Flex spaceItems={{ default: 'spaceItemsSm' }}>
      <FlexItem>
        <ExclamationCircleIcon />
      </FlexItem>
      <FlexItem>{children}</FlexItem>
    </Flex>
  </div>
);

export const TopBarWarning = ({ children }: React.PropsWithChildren<ITopBar>) => (
  <div className={`${styles['top-bar']} ${styles['top-bar-warning']}`}>{children}</div>
);

export const TopBarInfo = ({ children }: React.PropsWithChildren<ITopBar>) => (
  <div className={`${styles['top-bar']} ${styles['top-bar-info']}`}>{children}</div>
);
