import { useEffect, useState } from 'react';

interface IFieldValues {
  [key: string]: string;
}

interface IFieldErros {
  [key: string]: string;
}

interface IFieldValidators {
  [key: string]: Function;
}

export const useForm = (initValues: IFieldValues, validators: IFieldValidators) => {
  const [fieldValues, setFieldValues] = useState<IFieldValues>(initValues);
  const [fieldErrors, setFieldErrors] = useState<IFieldErros>({});
  const [fieldValidators, setFieldValidators] = useState<IFieldValidators>(validators);

  const initFieldStates = { ...initValues };

  const [fieldState, setFieldStates] = useState<any>({ ...initValues });
  useEffect(() => {}, [fieldValues]);

  const onChange = (event: React.FormEvent<HTMLInputElement>) => {
    setFieldValues({ ...fieldValues, [event.currentTarget.name]: event.currentTarget.value });
    console.log(fieldValues);
  };

  const onBlur = (event: React.FormEvent<HTMLInputElement>) => {
    const fieldName = event.currentTarget.name;
    const error = fieldValidators[fieldName](fieldValues[fieldName]);
    setFieldErrors({ ...fieldErrors, [event.currentTarget.name]: error });
  };

  return { fieldValues, fieldErrors, onChange, onBlur };
};
