import { Toolbar, ToolbarContent, ToolbarItem, Button } from '@patternfly/react-core';
import { LogViewer as LogViewerPF, LogViewerSearchProps } from '@patternfly/react-log-viewer';
import { useEffect, useRef, useState } from 'react';
import '../../index.css';
import { CaretDownIcon, CaretUpIcon } from '@patternfly/react-icons';

interface ILogViewerProps {
  data: string | string[];
  follow: boolean;
}

export const LogViewer = ({ data, follow }: ILogViewerProps) => {
  const logViewerRef = useRef<any>();
  const [renderData, setRenderData] = useState<any>([]);
  const [lineCount, setLineCount] = useState<any>(0);
  const [following, setFollowing] = useState<boolean>(follow);

  useEffect(() => {
    if (following) {
      setLineCount(data.length);
      logViewerRef?.current?.scrollToBottom();
    }
  }, [following, data]);

  return (
    <div className="p-10">
      <LogViewerPF
        innerRef={logViewerRef}
        scrollToRow={lineCount}
        data={data}
        overScanCount={10}
        toolbar={
          <Toolbar>
            <ToolbarContent>
              <ToolbarItem>
                <Button
                  onClick={() => {
                    logViewerRef?.current?.scrollTo(0, 0);
                  }}
                  variant="control"
                  icon={<CaretUpIcon />}
                >
                  Top
                </Button>
              </ToolbarItem>
              <ToolbarItem>
                <Button
                  onClick={() => {
                    logViewerRef?.current?.scrollToBottom();
                  }}
                  variant="control"
                  icon={<CaretDownIcon />}
                >
                  Bottom
                </Button>
              </ToolbarItem>
              <ToolbarItem>
                <Button
                  onClick={() => {
                    setFollowing(!following);
                  }}
                  variant="control"
                >
                  Follow
                </Button>
              </ToolbarItem>
            </ToolbarContent>
          </Toolbar>
        }
      />
    </div>
  );
};
