import { ScmRepositoriesList } from './../ScmRepositoriesList/ScmRepositoriesList';
import { PageLayout } from './../PageLayout/PageLayout';
import { DataContainer } from '../../containers/DataContainer/DataContainer';
import { IService, useDataContainer } from '../../containers/DataContainer/useDataContainer';
import { repositoryService } from '../../services/repositoryService';
import { Label, ToolbarItem } from '@patternfly/react-core';
import { Pagination } from '../Pagination/Pagination';
import { useQueryParamsEffect } from '../../containers/useQueryParamsEffect';
import { Filtering, IFilterOptions } from '../Filtering/Filtering';
import { Toolbar } from '../Toolbar/Toolbar';
import { useTitle } from '../../containers/useTitle';
import { PageTitles } from '../../utils/PageTitles';

interface IScmRepositoriesPage {
  componentId?: string;
}

export const ScmRepositoriesPage = ({ componentId = 'p1' }: IScmRepositoriesPage) => {
  const dataContainer = useDataContainer(({ requestConfig }: IService) => repositoryService.getRepositories(requestConfig));

  useTitle(PageTitles.repositories);

  const filterOptions: IFilterOptions = {
    filterAttributes: {
      internalUrl: {
        id: 'internalUrl',
        title: 'Internal URL',
        placeholder: 'string | !string | s?ring | st*ng',
        operator: '=like=',
      },
      externalUrl: {
        id: 'externalUrl',
        title: 'External URL',
        placeholder: 'string | !string | s?ring | st*ng',
        operator: '=like=',
      },
    },
  };

  const ToolbarItemWidths = {
    default: '100%',
  };

  useQueryParamsEffect((requestConfig: Object) => {
    dataContainer.refresh({ requestConfig });
  }, componentId);

  return (
    <PageLayout
      title="SCM Repositories"
      description={
        <>
          PNC works with 2 kinds of SCM servers - <Label>Internal</Label> and <Label>External</Label>. Internal server is a Gerrit
          server supported by Sys-ops. Any other SCM server except Gerrit is considered as External.
        </>
      }
    >
      <Toolbar>
        <ToolbarItem widths={ToolbarItemWidths}>
          <Filtering filterOptions={filterOptions} componentId={componentId} />
        </ToolbarItem>
      </Toolbar>

      <DataContainer {...dataContainer} title="SCM Repositories List">
        <ScmRepositoriesList repositories={dataContainer.data?.content} />
      </DataContainer>
      {/* Pagination need to be outside of DataContainer so that it can initialize Query Params */}
      <Pagination componentId={componentId} count={dataContainer.data?.totalHits} />
    </PageLayout>
  );
};
