import { Select, SelectOption, SelectVariant } from '@patternfly/react-core';
import { AxiosRequestConfig } from 'axios';
import { useCallback, useEffect, useRef, useState } from 'react';

interface ISearchSelectProps {
  fetchCallback: Function;
  attribute: string;
  onSelect: Function;
  delay?: number;
  pageSize?: number;
  displayDescription?: boolean;
}

/**
 * Filtered select with data dynamically fetched from backend.
 * Select options data are fetched:
 *  -> when select is firstly loaded
 *    -> these are buffered so they are not refetched
 *  -> when filter input text is changed (by typing or selecting option)
 *    -> these are filtered by attribute and current filter text value
 *
 * onSelect callback is used so selected option is accessible from outside.
 *
 * @param fetchCallback - function to fetch the data from backend
 * @param attribute - which attribute will be filtered
 * @param delay - delay after which data are fetched when filter input is changed
 * @param pageSize - count of entries fetched
 * @param displayDescription - should options display description? (if any)
 */
export const SearchSelect = ({
  fetchCallback,
  attribute,
  onSelect,
  delay = 200,
  pageSize = 20,
  displayDescription = false,
}: ISearchSelectProps) => {
  // newest (filtered) data downloaded using callback
  const [currentData, setCurrentData] = useState<any[]>([]);
  // data downloaded on first load
  const [defaultData, setDefaultData] = useState<any[]>([]);
  //
  const [selected, setSelected] = useState<string | undefined>();
  const [isSelectOpen, setIsSelectOpen] = useState<boolean>(false);
  const timeout = useRef<NodeJS.Timeout>();

  const fetchData = useCallback(
    (val: string = '', setDefault: boolean = false) => {
      const config: AxiosRequestConfig = { params: { pageSize } };
      if (val !== '') config.params.q = `${attribute}=like="%${val}%"`;
      fetchCallback(config).then((response: any) => {
        const data = response.data.content;
        setCurrentData(data);
        if (setDefault) setDefaultData(data);
        return data;
      });
    },
    [fetchCallback, attribute, pageSize]
  );

  useEffect(() => {
    fetchData('', true);
  }, [fetchData]);

  const clearSelection = () => {
    setSelected(undefined);
    setIsSelectOpen(false);
    setCurrentData(defaultData);
  };

  return (
    <Select
      variant={SelectVariant.typeahead}
      onToggle={(isOpen) => {
        setIsSelectOpen(isOpen);
      }}
      onSelect={(event, selection, isPlaceholder) => {
        if (isPlaceholder) clearSelection();
        else {
          if (event) {
            fetchData(selection as string);
          }
          setSelected(selection as string);
          setIsSelectOpen(false);
          onSelect(selection);
        }
      }}
      onTypeaheadInputChanged={(val: string) => {
        if (val !== '') {
          clearTimeout(timeout?.current);
          timeout.current = setTimeout(() => fetchData(val), delay);
        } else {
          setCurrentData(defaultData);
        }
      }}
      isInputFilterPersisted={true}
      isInputValuePersisted={true}
      onClear={clearSelection}
      onFilter={() => {
        // filtering is not done here
        return undefined;
      }}
      selections={selected}
      isOpen={isSelectOpen}
      hasInlineFilter={true}
    >
      {currentData.map((option: any, index: any) => (
        <SelectOption key={index} description={displayDescription && option['description']} value={option[attribute]} />
      ))}
    </Select>
  );
};
