import { MutableRefObject, useCallback, useEffect, useRef, useState } from 'react';

type PreprocessorFunction = (lines: string[]) => string[];

/**
 * Buffer of data with delay.
 * Data are in the format of array of string.
 *
 * Provides a way to delay inputted data before they are used.
 * Data can be preprocessed by preprocessor function, which processes inputted lines as programmer wishes.
 *
 * To add new lines, use addLines function. After that, they will be preprocessed with preprocessor function.
 * Each added group of lines is treated as a batch and outputted together.
 * Buffer periodically (with delay) checks if any new lines were added, and if so, it will add oldest batch of lines not yet ouputted.
 * Then next batch will be outputted (if any)
 *
 * @param preprocessor - function by which each added line is modified
 */
export const useDataBuffer = (preprocessor: PreprocessorFunction = (lines: string[]) => lines) => {
  const [data, setData] = useState<string[]>([]);
  const [buffer, setBuffer] = useState<string[]>([]);
  const [currentLineCount, setCurrentLineCount] = useState<number>(0);
  const savedTimer: MutableRefObject<NodeJS.Timer | undefined> = useRef();

  const inputCnts = useRef<number[]>([]);
  const currInput = useRef<number>(0);

  useEffect(() => {
    savedTimer.current = setInterval(() => {
      if (inputCnts.current.length > currInput.current) {
        setCurrentLineCount((currentLineCount) => currentLineCount + inputCnts.current[currInput.current]);
        currInput.current += 1;
      }
    }, 1500);
  }, []);

  // useEffect(() => {
  //   if (data.length > buffer.length) {
  //     savedTimer.current = setTimeout(() => {
  //       setCurrentLineCount((currentLineCount) => currentLineCount + 5);
  //     }, 1500);
  //   }
  //   return () => {
  //     clearTimeout(savedTimer.current);
  //   };
  // }, [data.length, buffer.length]);

  useEffect(() => {
    if (data.length > buffer.length) setBuffer(data.slice(0, currentLineCount));
    // if (currentLineCount > initLogData.length) {
    //   clearInterval(savedTimer.current);
    // } else {
    //   // setLogData(initLogData.slice(0, currentLineCount));
    //   addLines(initLogData.slice(currentLineCount - 3, currentLineCount));
    // }
  }, [currentLineCount]);

  // useEffect(() => {
  //   setTimeout(() => {
  //     setBuffer(data);
  //   }, 3000);
  // }, [data]);

  const addLines = useCallback(
    (lines: string[]) => {
      setData([...data, ...preprocessor(lines)]);
      inputCnts.current.push(lines.length);
    },
    [data, preprocessor]
  );

  return { buffer, addLines };
};
