import { useCallback, useState } from 'react';

export const useDataBuffer = () => {
  const [data, setData] = useState<any>([]);
  const [buffer, setBuffer] = useState<any>([]);

  const addLines = (lines: string[]) => {
    setData([...data, ...lines]);
    setTimeout(() => {
      setBuffer(data);
    }, 1500);
  };

  return { buffer, addLines };
};
