import { Select, SelectOption, SelectVariant } from '@patternfly/react-core';
import { useEffect, useRef, useState } from 'react';

interface ISearchSelect {
  attribute: string;
  callback: Function;
}

export const SearchSelect = ({ attribute, callback }: ISearchSelect) => {
  const [selectOptions, setSelectOptions] = useState<any>([]);
  const [isSelectOpen, setIsSelectOpen] = useState<boolean>(false);
  const [selected, setSelected] = useState<any>();
  const [loading, setLoading] = useState<boolean>(false);
  const timer = useRef<any>();

  useEffect(() => {
    setLoading(true);
    callback({ params: { pageSize: 20 } }).then((response: any) => {
      const obj = response.data.content
        .map((obj: any) => {
          return {
            value: obj[attribute],
          };
        })
        .map((option: any, index: any) => <SelectOption key={index} value={option.value} />);
      setSelectOptions(obj);
      setLoading(false);
    });
  }, [callback, attribute]);

  const clearSelection = () => {
    setSelected(null);
    setIsSelectOpen(false);
    setLoading(true);
    callback({ params: { pageSize: 20 } }).then((response: any) => {
      const obj = response.data.content
        .map((obj: any) => {
          return {
            value: obj[attribute],
          };
        })
        .map((option: any, index: any) => <SelectOption key={index} value={option.value} />);
      setSelectOptions(obj);
      setLoading(false);
    });
  };

  return (
    <Select
      variant={SelectVariant.typeahead}
      onToggle={(isOpen) => {
        setIsSelectOpen(isOpen);
      }}
      onSelect={(event, selection, isPlaceholder) => {
        console.log(selection);
        if (isPlaceholder) clearSelection();
        else {
          setSelected(selection);
          setIsSelectOpen(false);
          setSelectOptions([<SelectOption key={0} value={selection} />]);
        }
      }}
      onTypeaheadInputChanged={(val: string) => {
        clearTimeout(timer?.current);
        timer.current = setTimeout(() => {
          setLoading(true);
          callback({ params: { pageSize: 20, q: `${attribute}=like="%${val}%"` } }).then((response: any) => {
            setSelectOptions(
              response.data.content
                .map((obj: any) => {
                  return {
                    value: obj[attribute],
                  };
                })
                .map((option: any, index: any) => <SelectOption key={index} value={option.value} />)
            );
            setLoading(false);
          });
        }, 200);
      }}
      isInputFilterPersisted={true}
      isInputValuePersisted={true}
      onClear={clearSelection}
      onFilter={() => {
        return selectOptions;
      }}
      selections={selected}
      isOpen={isSelectOpen}
      hasInlineFilter={true}
    >
      {selectOptions}
    </Select>
  );
};
