import { useEffect, useState } from 'react';

interface IFieldValues {
  [key: string]: string;
}

interface IFieldErros {
  [key: string]: string;
}

interface IFieldValidators {
  [key: string]: Function;
}

export const useForm = (initValues: IFieldValues) => {
  const [fieldValues, setFieldValues] = useState<IFieldValues>({});
  const [fieldErrors, setFieldErrors] = useState<IFieldErros>({});
  const [fieldValidators, setFieldValidators] = useState<IFieldValidators>({});

  useEffect(() => {}, [fieldValues]);

  const onChange = (event: React.FormEvent<HTMLInputElement>) => {
    setFieldValues({ ...fieldValues, [event.currentTarget.name]: event.currentTarget.value });
  };

  return {
    fieldValues: fieldValues,
  };
};
