import { Button, InputGroup, Select, SelectOption, SelectVariant } from '@patternfly/react-core';
import { SortAmountDownAltIcon, SortAmountDownIcon } from '@patternfly/react-icons';
import { useCallback, useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

import { getComponentQueryParamValue, updateQueryParamsInURL } from '../../utils/queryParamsHelper';

import { IFilterAttribute, IFilterOptions } from '../Filtering/Filtering';

interface ISortingProps {
  filterOptions: IFilterOptions;
  componentId: any;
}

export const Sorting = ({ filterOptions, componentId }: ISortingProps) => {
  const location = useLocation();
  const navigate = useNavigate();

  const defaultAttributeKey = Object.keys(filterOptions.filterAttributes)[0];

  const [sortAttribute, setSortAttribute] = useState<IFilterAttribute>(filterOptions.filterAttributes[defaultAttributeKey]);
  const [isSortAttributeOpen, setIsSortAttributeOpen] = useState<boolean>(false);
  const [ascending, setAscending] = useState<boolean | null>(null);

  const [isSorting, setIsSorting] = useState<boolean>(false);

  /**
   * Add sort filter by updating URL.
   */
  const addSortFilter = useCallback(
    (filterAttribute: string, order: 'asc' | 'desc', replace: boolean = false) => {
      updateQueryParamsInURL({ sort: `=${order}=${filterAttribute}`, pageIndex: 1 }, componentId, location, navigate, replace);
    },
    [componentId, location, navigate]
  );

  useEffect(() => {
    const currentSortParam = getComponentQueryParamValue(location.search, 'sort', componentId) || '';
    if (!currentSortParam) {
      addSortFilter(defaultAttributeKey, 'asc', true);
    }

    const sortOrder = currentSortParam.split('=')?.[1];
    const sortParam = currentSortParam.split('=')?.[2];

    if (currentSortParam) {
      setAscending(sortOrder === 'asc');
      setSortAttribute(filterOptions.filterAttributes[sortParam]);
    } else {
      setAscending(null);
      setSortAttribute(filterOptions.filterAttributes[defaultAttributeKey]);
    }
  }, [location.search, location, componentId, navigate, filterOptions.filterAttributes, defaultAttributeKey, addSortFilter]);

  return (
    <InputGroup>
      <Select
        variant={SelectVariant.single}
        onToggle={(isOpen) => {
          setIsSortAttributeOpen(isOpen);
        }}
        onSelect={(event, selection, isPlaceholder) => {
          if (!isPlaceholder) {
            setIsSortAttributeOpen(false);
            addSortFilter((selection as IFilterAttribute)?.id, ascending ? 'asc' : 'desc');
          }
        }}
        selections={sortAttribute}
        isOpen={isSortAttributeOpen}
      >
        {Object.keys(filterOptions.filterAttributes)
          .filter((filterAttributeKey: string) => {
            return filterOptions.filterAttributes[filterAttributeKey]?.sorted;
          })
          .map((filterAttributeKey: string) => {
            const filterAttribute = filterOptions.filterAttributes[filterAttributeKey];
            // use 'title' attribute as default
            filterAttribute.toString = () => {
              return filterAttribute.title;
            };

            return <SelectOption key={filterAttribute.id} value={filterAttribute} />;
          })}
      </Select>
      <Button
        onClick={() => {
          addSortFilter(sortAttribute.id, ascending === null || !ascending ? 'asc' : 'desc');
        }}
        variant="plain"
        icon={ascending === null || ascending ? <SortAmountDownAltIcon /> : <SortAmountDownIcon />}
      ></Button>
      <Button style={{ padding: '1px' }} variant="plain" isInline>
        Remove sorting
      </Button>
    </InputGroup>
  );
};
