import styles from './TopBar.module.css';
import { Button, Flex, FlexItem } from '@patternfly/react-core';
import { ExclamationCircleIcon, ExclamationTriangleIcon, InfoCircleIcon, TimesIcon } from '@patternfly/react-icons';
import { useEffect, useRef, useState } from 'react';

interface ITopBarProps {
  topBarClass: string;
  icon: any;
}

interface ITopBarSpecialized {}

export const TopBar = ({ children, topBarClass, icon }: React.PropsWithChildren<ITopBarProps>) => {
  const [isOpen, setIsOpen] = useState<boolean>(true);
  const firstRender = useRef(true);

  useEffect(() => {
    const isTopBarOpen = window.sessionStorage.getItem(topBarClass);
    setIsOpen(isTopBarOpen !== 'closed');
  }, [topBarClass]);

  useEffect(() => {
    if (!firstRender.current) {
      window.sessionStorage.setItem(topBarClass, 'open');
      setIsOpen(true);
    }
  }, [children, topBarClass]);

  return isOpen ? (
    <div className={`${styles['top-bar']} ${styles[topBarClass]}`}>
      <Flex justifyContent={{ default: 'justifyContentSpaceBetween' }}>
        <FlexItem>
          <Flex spaceItems={{ default: 'spaceItemsSm' }}>
            <FlexItem>{icon}</FlexItem>
            <FlexItem>{children}</FlexItem>
          </Flex>
        </FlexItem>
        <FlexItem>
          <Button
            onClick={() => {
              setIsOpen(false);
              window.sessionStorage.setItem(topBarClass, 'closed');
            }}
            variant="plain"
          >
            <TimesIcon className={styles['close-icon']} />
          </Button>
        </FlexItem>
      </Flex>
    </div>
  ) : null;
};

export const TopBarError = ({ children }: React.PropsWithChildren<ITopBarSpecialized>) => (
  <TopBar topBarClass="top-bar-error" icon={<ExclamationCircleIcon />}>
    {children}
  </TopBar>
);

export const TopBarWarning = ({ children }: React.PropsWithChildren<ITopBarSpecialized>) => (
  <TopBar topBarClass="top-bar-warning" icon={<ExclamationTriangleIcon />}>
    {children}
  </TopBar>
);

export const TopBarInfo = ({ children }: React.PropsWithChildren<ITopBarSpecialized>) => (
  <TopBar topBarClass="top-bar-info" icon={<InfoCircleIcon />}>
    {children}
  </TopBar>
);
