import { MutableRefObject, useCallback, useEffect, useRef, useState } from 'react';

export const useDataBuffer = () => {
  const [data, setData] = useState<any>([]);
  const [buffer, setBuffer] = useState<any>([]);
  const [currentLineCount, setCurrentLineCount] = useState<number>(1);
  const savedTimer: MutableRefObject<NodeJS.Timer | undefined> = useRef();

  useEffect(() => {
    savedTimer.current = setInterval(() => {
      setCurrentLineCount((data: any) => data.length);
    }, 5000);
    return () => {
      clearInterval(savedTimer.current);
    };
  }, []);

  useEffect(() => {
    // setBuffer(data);
    console.log(currentLineCount);
  }, [currentLineCount]);

  const addLines = useCallback(
    (lines: string[]) => {
      setData([...data, ...lines]);
    },
    [data]
  );

  return { buffer, addLines };
};
