import { Toolbar, ToolbarContent, ToolbarItem, Button } from '@patternfly/react-core';
import { LogViewer as LogViewerPF } from '@patternfly/react-log-viewer';
import { useRef, useState } from 'react';
import '../../index.css';
import { CaretDownIcon, CaretUpIcon } from '@patternfly/react-icons';

interface ILogViewerProps {
  data: string | string[];
  follow: boolean;
}

export const LogViewer = ({ data, follow }: ILogViewerProps) => {
  const logViewerRef = useRef();
  const [following, setFollowing] = useState<boolean>(follow);

  return (
    <div className="p-10">
      <LogViewerPF
        ref={logViewerRef}
        data={data}
        toolbar={
          <Toolbar>
            <ToolbarContent>
              <ToolbarItem>
                <Button onClick={() => {}} variant="control" icon={<CaretDownIcon />}>
                  Top
                </Button>
              </ToolbarItem>
              <ToolbarItem>
                <Button onClick={() => {}} variant="control" icon={<CaretUpIcon />}>
                  Bottom
                </Button>
              </ToolbarItem>
              <ToolbarItem>
                <Button onClick={() => {}} variant="control">
                  Follow
                </Button>
              </ToolbarItem>
            </ToolbarContent>
          </Toolbar>
        }
      />
    </div>
  );
};
