import '@patternfly/react-core/dist/styles/base.css';

import { Table, TableHeader, TableBody, cellWidth } from '@patternfly/react-table';
import { SCMRepository } from 'pnc-api-types-ts';
import { Link } from 'react-router-dom';

import { ProjectLink } from '../ProjectLink/ProjectLink';

interface IScmRepositoriesList {
  repositories: SCMRepository[];
}

/**
 * Dump component displaying list of SCM repositories.
 *
 * @param repositories - List of SCM repositories to be displayed
 */
export const ScmRepositoriesList = ({ repositories }: IScmRepositoriesList) => {
  const columns = [
    { title: 'Name', transforms: [cellWidth(30)] },
    'Internal SCM URL',
    'External SCM URL',
    { title: 'Pre-build Sync', transforms: [cellWidth(15)] },
  ];

  const repoRegex = /[^\/]+\/[^\/]+.git$/i;

  const rows = repositories.map((repository: SCMRepository) => [
    repository.internalUrl.match(repoRegex)?.[0],
    repository.internalUrl,
    repository.externalUrl,
    String(repository.preBuildSyncEnabled),
  ]);

  return (
    <Table aria-label="SCM repositories List" variant="compact" borders={false} cells={columns} rows={rows}>
      <TableHeader />
      <TableBody />
    </Table>
  );
};
