import { MutableRefObject, useCallback, useEffect, useRef, useState } from 'react';

export const useDataBuffer = (preprocessor: Function = (lines: string[]) => lines) => {
  const [data, setData] = useState<string[]>([]);
  const [buffer, setBuffer] = useState<string[]>([]);
  const [currentLineCount, setCurrentLineCount] = useState<number>(1);
  const savedTimer: MutableRefObject<NodeJS.Timer | undefined> = useRef();

  // useEffect(() => {
  //   if (data.length > buffer.length) {
  //     savedTimer.current = setTimeout(() => {
  //       setCurrentLineCount((currentLineCount) => currentLineCount + 5);
  //     }, 1500);
  //   }
  //   return () => {
  //     clearTimeout(savedTimer.current);
  //   };
  // }, [data.length, buffer.length]);

  // useEffect(() => {
  //   setBuffer(data.slice(0, currentLineCount));
  //   // if (currentLineCount > initLogData.length) {
  //   //   clearInterval(savedTimer.current);
  //   // } else {
  //   //   // setLogData(initLogData.slice(0, currentLineCount));
  //   //   addLines(initLogData.slice(currentLineCount - 3, currentLineCount));
  //   // }
  // }, [currentLineCount]);

  // useEffect(() => {
  //   setTimeout(() => {
  //     setBuffer(data);
  //   }, 3000);
  // }, [data]);

  const addLines = useCallback(
    (lines: string[]) => {
      setData([...data, ...preprocessor(lines)]);
      savedTimer.current = setTimeout(() => {
        setCurrentLineCount((currentLineCount) => currentLineCount + 5);
      }, 1500);
    },
    [data, preprocessor]
  );

  return { buffer, addLines };
};
