import { Toolbar, ToolbarContent, ToolbarItem, Button, Switch } from '@patternfly/react-core';
import { LogViewer as LogViewerPF, LogViewerSearchProps } from '@patternfly/react-log-viewer';
import { useEffect, useRef, useState } from 'react';
import '../../index.css';
import { CaretDownIcon, CaretUpIcon } from '@patternfly/react-icons';

interface ILogViewerProps {
  data: string | string[];
  follow: boolean;
}

export const LogViewer = ({ data, follow }: ILogViewerProps) => {
  const logViewerRef = useRef<any>();
  // line count assigned to 'scrollToRow' makes it scroll to bottom properly
  const [lineCount, setLineCount] = useState<any>(0);
  const [following, setFollowing] = useState<boolean>(follow);

  const [isPaused, setIsPaused] = useState(false);
  const [itemCount, setItemCount] = useState(1);
  const [currentItemCount, setCurrentItemCount] = useState(0);
  const [buffer, setBuffer] = useState(data);
  const [linesBehind, setLinesBehind] = useState(0);

  useEffect(() => {
    if (itemCount > selectedData.length) {
      window.clearInterval(timer);
    } else {
      setBuffer(selectedData.slice(0, itemCount));
    }
  }, [itemCount]);

  React.useEffect(() => {
    if (!isPaused && buffer.length > 0) {
      setCurrentItemCount(buffer.length);
      setRenderData(buffer.join('\n'));
      if (logViewerRef && logViewerRef.current) {
        logViewerRef.current.scrollToBottom();
      }
    } else if (buffer.length !== currentItemCount) {
      setLinesBehind(buffer.length - currentItemCount);
    } else {
      setLinesBehind(0);
    }
  }, [isPaused, buffer]);

  useEffect(() => {
    if (following) {
      setLineCount(data.length);
      logViewerRef?.current?.scrollToBottom();
    }
  }, [following, data]);

  return (
    <div className="p-10">
      <LogViewerPF
        innerRef={logViewerRef}
        scrollToRow={following ? lineCount : 0}
        data={data}
        toolbar={
          <Toolbar>
            <ToolbarContent>
              <ToolbarItem>
                <Button
                  onClick={() => {
                    logViewerRef?.current?.scrollTo(0, 0);
                  }}
                  variant="control"
                  icon={<CaretUpIcon />}
                >
                  Top
                </Button>
              </ToolbarItem>
              <ToolbarItem>
                <Button
                  onClick={() => {
                    logViewerRef?.current?.scrollToBottom();
                  }}
                  variant="control"
                  icon={<CaretDownIcon />}
                >
                  Bottom
                </Button>
              </ToolbarItem>
              <ToolbarItem>
                <Switch
                  label="Following"
                  labelOff="Not Following"
                  isChecked={following}
                  onChange={(checked) => {
                    setFollowing(checked);
                  }}
                />
              </ToolbarItem>
            </ToolbarContent>
          </Toolbar>
        }
      />
    </div>
  );
};
