import { Button, InputGroup, Select, SelectOption, SelectVariant } from '@patternfly/react-core';
import { SortAmountDownAltIcon, SortAmountDownIcon } from '@patternfly/react-icons';
import { useState } from 'react';

interface ISortingProps {
  sortOptions: any;
  componentId: any;
}

export const Sorting = ({ sortOptions, componentId }: ISortingProps) => {
  const [sortAttribute, setSortAttribute] = useState<any>(sortOptions[0]);
  const [isSortAttributeOpen, setIsSortAttributeOpen] = useState<boolean>(false);
  const [sortedUp, setSortedUp] = useState<boolean>(false);

  return (
    <InputGroup>
      <Select
        width="100px"
        variant={SelectVariant.single}
        onToggle={(isOpen) => {
          setIsSortAttributeOpen(isOpen);
        }}
        onSelect={(event, selection, isPlaceholder) => {
          if (!isPlaceholder) {
            setSortAttribute(selection as any);
            setIsSortAttributeOpen(false);
          }
        }}
        selections={sortAttribute}
        isOpen={isSortAttributeOpen}
      >
        {sortOptions.map((val: string) => {
          return <SelectOption key={val} value={val} />;
        })}
      </Select>
      <Button
        onClick={() => {
          setSortedUp(!sortedUp);
        }}
        variant="plain"
        icon={sortedUp ? <SortAmountDownIcon /> : <SortAmountDownAltIcon />}
      ></Button>
    </InputGroup>
  );
};
